// Pets.js

import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../Pets.css'

import Header from './Header.js';
import Footer from './Footer.js';

const PetObject = props => (
    <Link id="PetLink" to="/Pets">
        <div id="PetContainer">
            <div id="ImageCircle">
                <p id="PetText">Add Pet</p>
            </div>
            <div>
                <p id="NameText">{props.petObject.PetName}</p>
            </div>
        </div>
    </Link>
)

export default class Pets extends Component {

    state = { pets: [] }

    componentDidMount() {
        axios.get("http://localhost:5000/api/pets/")
            .then(response=>{
                const pets = response.data;
                this.setState({ pets });
            })
            .catch((error) => {
                console.log(error);
            })
    }

    render() {
    return (
        <div className="fullPageContainer">

        <div>
          <Header />
        </div>
        <div className="mainPageBody nopadding">
          <Container fluid>
            <Row>
              <Col xs="2" className="nopadding">
                <div className="mainPageSideBtnGroup">
                  <button className="btn-mainpage-side">
                    Records
                  </button><br />
                  <button className="btn-mainpage-side">
                    Health
                  </button><br />
                  <button className="btn-mainpage-side">
                    Event
                  </button><br />
                  <button className="btn-mainpage-side">
                    Reminders
                  </button>

                </div>
              </Col>

              <Col lg={true} className="nopadding">
                <div className="mainPageContents">
                    { this.state.pets.map(pet => 
                    <Link id="PetLink" to="/Pets">
                        <div id="PetContainer">
                            <div id="ImageCircle">
                                <p id="PetText">Add Pet</p>
                            </div>
                            <div>
                                <p id="NameText">{pet.PetName}</p>
                            </div>
                        </div>
                    </Link>
                    )}
                </div>
              </Col>
            </Row>
          </Container>
        </div>
        <div className="mainPageFooter">
          <Footer />
        </div> 
      </div>
    )
    }
}

//export default Pets
