// Pets.js

import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';
import { storage } from "../firebase";

import '../Pets.css'

import Header from './Header.js';
import Footer from './Footer.js';
import FileUpload from './FileUpload.js';

import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
//import { pet } from '../../../models';


const PetObject = props => (
    <Link id="PetLink" to="/Pets">
        <div id="PetContainer">
            <div id="ImageCircle">
                <p id="PetText">Add Pet</p>
            </div>
            <div>
                <p id="NameText">{props.petObject.PetName}</p>
                
            </div>
        </div>
    </Link>
)

function getImage(image) {
  storage.child(`PetImages/${image}.png`).getDownloadURL().then((url) => {
    this.state["petImages"] = url
    this.setState(this.state)
  })
}

function HasProfilePic(props) {

  // Check if file exists
  if (getImage(String(props.account.AccountId) + String(props.pet.PetId)))

  return <img src="PetRecs Icon.png" id="ImageCircle"></img>;

  return <p id="PetText">Add Pet</p>;
}

export default class Pets extends Component {

    state = { pets: [], 
              account: {},
              petImages: [] }

    componentDidMount() {

        axios.all([axios.get("http://localhost:5000/api/pets/")])
        //axios.get("http://localhost:5000/api/pets/")
        //    .then(response=>{
        //        const pets = response.data;
        //        this.setState({ pets });
        //    })
        //    .catch((error) => {
        //        console.log(error);
        //    })

        axios.get("http://localhost:5000/api/accounts/1/")
            .then(response=>{
                const accountInfo = response.data;
                this.setState({ account: accountInfo });
                console.log(this.state);
            })
            .catch((error) => {
                console.log(error);
            })
    }



    render() {
    const { account } = this.state.account;
    return (
        <div className="fullPageContainer">
          <div>
            <Header />
          </div>
          <Container fluid>
            <div className="mainPageBody nopadding">
                <div className="mainPageContents">
                  { this.state.pets.map(pet => 
                  <Link id="PetLink" to={"/Pets/Profile/" + pet.PetId}>
                    <div id="PetContainer">
                      <div id="CircleShape">
                        <HasProfilePic />
                      </div>
                      <div>
                          <p id="NameText">{pet.PetName + account.FirstName}</p>
                      </div>
                    </div>
                  </Link>
                  )}
                </div>
            </div>
          </Container>
          <div className="mainPageFooter">
            <Footer />
          </div> 
        </div>
    )
    }
}

//export default Pets
