// HomePage.js

import React, { Component } from 'react';
import { render } from 'react-dom';
import { Link } from 'react-router-dom';

class App extends Component {

    constructor() {
        super();
        this.state = {
            name: "React",
            showHideLogin: false,
            showHideRegister: false
        };
        this.hideComponent = this.hisComponent.bind(this);
    }

    hideComponent(name) {
        console.log(name);
        switch (name) {
          case "showHideLogin":
            this.setState({ showHideLogin: !this.state.showHideLogin });
            break;
          case "showHideRegister":
            this.setState({ showHideRegister: !this.state.showHideRegister });
            break;
          default:
            null;
        }
      }
    
      render() {
        const { showHideLogin, showHideRegister } = this.state;
        return (
          <div>
            {showHideLogin && <Demo1 />}
            <hr />
            {showHideRegister && <Demo2 />}
            <hr />
            <div>
              <button onClick={() => this.hideComponent("showHideLogin")}>
                Click to hide Demo1 component
              </button>
              <button onClick={() => this.hideComponent("showHideRegister")}>
                Click to hide Demo2 component
              </button>
            </div>
          </div>
        );
      }
    }
