// Register.js

import React from 'react'
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { useForm } from "react-hook-form"

import { Link } from 'react-router-dom';

function Register() {

    const { register, handleSubmit, errors } = useForm();
    const onSubmit = (data) => console.log(data)

    const accountOptions = [
        { value: '1', label: 'Pet Owner' },
        { value: '2', label: 'Veterinarian'}
    ];

    return (
        <div className="fill-window fullPageContainer">
            <div className="loginRegPageHeader">
                <div className="homePageHeaderItem">
                    <img src={require('../pet-recs-logo_low-qual.png')} width='48' height='55' />
                    PetRecs
                </div>
            </div>
            <div className="formBox">
                <div className="formTitle">
                    Create an Account
                </div>
                <Form onSubmit={handleSubmit(onSubmit)} className="loginRegForm">
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>E-Mail Address</Form.Label>
                        <Form.Control name="email" type="email" placeholder="example@mail.com" ref={register({ required: true })}/>
                        {errors.email && "Email is required"}
                    </Form.Group>

                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control name="password" type="password" placeholder="Password" ref={register({ required: true })} />
                    </Form.Group>

                    <Form.Group controlId="formBasicRepeatPassword">
                        <Form.Label>Repeat Password</Form.Label>
                        <Form.Control name="password_confirm" type="password" placeholder="Password" ref={register({ required: true })} />
                    </Form.Group>

                    <Form.Group controlId="formBasicAccountType">
                        <Form.Label>Account Type</Form.Label>
                        <Form.Control as={"select"} ref={register({ required: true })}> {
                                accountOptions.map((option, label) => {
                                    return (<option key={label} value={option.label}>{option.label}</option>)
                                })
                            }
                        </Form.Control>
                    </Form.Group>

                    <br />
                    <Button variant="secondary" size="sm" className="btn-form"  type="submit">Register</Button>
                </Form>
                
                <br /><br />
                <Link to="/" className="btn btn-secondary">Register</Link>
            </div>
        </div>
    )
}

export default Register
