// Register.js

import React, {Component} from 'react'
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { useForm } from "react-hook-form"

import { Link } from 'react-router-dom';

const { register, handleSubmit, errors } = useForm();
const onSubmit = (data) => console.log(data);

const accountOptions = [
    { value: '1', label: 'Pet Owner' },
    { value: '2', label: 'Veterinarian'}
];

export class Register extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',
            accountType: '',
            msg: ''
        }
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    addFormData(evt) {
        evt.preventDefault();
        const fd = new FormData();
        fd.append('myUsername', this.refs.myUsername.value);
        fd.append('myEmail', this.refs.myEmail.value);
        var headers = {
            'Content-Type': 'application/json;charset=UTF-8',
            "Access-Control-Allow-Origin": "*"
        }
        axios.post('http://localhost/core_php.php', fd, headers
        ).then(res => {
                alert(res.data.data);
            }
        );
    }

    render()
    {
        return (
            <div className="fill-window fullPageContainer">
                <div className="loginRegPageHeader">
                    <div className="homePageHeaderItem">
                        <img src={require('../pet-recs-logo_low-qual.png')} width='48' height='55'/>
                        PetRecs
                    </div>
                </div>
                <div className="formBox">
                    <div className="formTitle">
                        Create an Account
                    </div>
                    <Form onSubmit={handleSubmit(onSubmit)} className="loginRegForm">
                        <Form.Group controlId="formBasicEmail">
                            <Form.Label>E-Mail Address</Form.Label>
                            <Form.Control name="email" type="email" placeholder="example@mail.com"
                                          ref={register({required: true})}/>
                            {errors.email && "Email is required"}
                        </Form.Group>

                        <Form.Group controlId="formBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control name="password" type="password" placeholder="Password"
                                          ref={register({required: true})}/>
                            {errors.password && "Password is required"}
                        </Form.Group>

                        <Form.Group controlId="formBasicRepeatPassword">
                            <Form.Label>Repeat Password</Form.Label>
                            <Form.Control name="password_confirm" type="password" placeholder="Password"
                                          ref={register({required: true})}/>
                            {errors.password_confirm && "Repeat Password is required"}
                        </Form.Group>

                        <Form.Group controlId="formBasicAccountType">
                            <Form.Label>Account Type</Form.Label>
                            <Form.Control name="accountType" as={"select"} ref={register({required: true})}> {
                                accountOptions.map((option, label) => {
                                    return (<option key={label} value={option.label}>{option.label}</option>)
                                })
                            }
                            </Form.Control>
                            {errors.accountType && "Account Type is required"}
                        </Form.Group>

                        <br/>
                        <Button variant="secondary" size="sm" className="btn-form" onClick={this.addFormData}
                                type="submit">Register</Button>
                    </Form>

                    <br/><br/>
                    <Link to="/" className="btn btn-secondary">Register</Link>
                </div>
            </div>
        )
    }
}

export default Register
