// Pets.js

import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';
import { storage } from "../firebase";

import '../Pets.css'

import Header from './Header.js';
import Footer from './Footer.js';
import FileUpload from './FileUpload.js';

import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
//import { pet } from '../../../models';

function HasProfilePic(props) {

  var imageRef = storage.child(`PetImages/`+String(props.AccountId) + String(props.PetId)+`.png`);

  imageRef.getDownloadURL().then((url) => {
    var img = document.getElementById('ImageCircle');
    img.src = url;
    return url;
  }).catch((error) => {
    var img = document.getElementById('ImageCircle');
    img.src = url;
      return "https://firebasestorage.googleapis.com/v0/b/petrecs-file-system.appspot.com/o/PetImages%2FPetRecsDefault.png?alt=media&token=eaa9f503-8783-4a18-ae32-c53bd67a5fc4";
  })
  return null;
}

export default class Pets extends Component {

    state = { pets: [], 
              account: {},
              petImages: [] }

    componentDidMount() {
      Promise.all([
        axios.get("/api/pets/", { withCredentials: true }), 
        axios.get("/api/accounts/1/", { withCredentials: true })
      ]).then(([result1, result2]) => {
          const pets = result1.data;
          const account = result2.data;

          this.setState({  pets, account });
        })
        .catch((error) => {
          console.log(error);
        })
    }



    render() {
    const account = this.state.account;
    return (
        <div className="fullPageContainer">
          <div>
            <Header />
          </div>
          <Container fluid>
            <div className="mainPageBody nopadding">
                <div className="mainPageContents">
                  { this.state.pets.map(pet => 
                  <Link id="PetLink" to={"/Pets/Profile/" + pet.PetId}>
                    <div id="PetContainer">
                      <div id="CircleShape">
                        <img  id="ImageCircle"></img>
                        {<HasProfilePic AccountId={account.AccountId} PetId={pet.PetId}/>}
                      </div>
                      <div>
                          <p id="NameText">{pet.PetName}</p> 
                      </div>
                    </div>
                  </Link>
                  )}
                </div>
            </div>
          </Container>
          <div className="mainPageFooter">
            <Footer />
          </div> 
        </div>
    )
    }
}

//export default Pets
