import React, {useState} from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

import HomePage from './components/HomePage'
import Login from './components/Login'
import Register from './components/Register'
import About from './components/About'
import Pets from './components/Pets'
import PetProfile from './components/PetProfile'
import PetRecords from './components/PetRecords'

import AuthApi from "./utils/AuthApi";
import axios from "axios";
function App(){

    const {auth, setAuth} = useState(false);

    const loggedIn = () => {
        axios.get("http://localhost:5000/api/sessions", { withCredentials: true })
            .then(response=>{
                setAuth(true);
            })
            .catch((error) => {
                console.log(error);
            })
    }

  return (
    <Router>
      <Route path="/" exact component={HomePage} />
      <Route path="/login" exact component={Login} />
      <Route path="/register" exact component={Register} />
      { loggedIn() &&
          <Route path="/about" exact component={About}/>

      }
      <Route path="/pets" exact component={Pets} />
      <Route path="/petprofile" exact component={PetProfile} />
      <Route path="/petrecords" exact component={PetRecords} />
    </Router>
  );
}

export default App;
