// Pets.js

import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';
import { storage } from "../firebase";

import '../Pets.css'

import Header from './Header.js';
import Footer from './Footer.js';
import FileUpload from './FileUpload.js';

import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
//import { pet } from '../../../models';


const PetObject = props => (
    <Link id="PetLink" to="/Pets">
        <div id="PetContainer">
            <div id="ImageCircle">
                <p id="PetText">Add Pet</p>
            </div>
            <div>
                <p id="NameText">{props.petObject.PetName}</p>
                
            </div>
        </div>
    </Link>
)

function getImage(image) {

  var imageRef = storage.child(`PetImages/${image}.png`);
  var defaultImageRef = storage.child(`PetImages/PetRecsDefault.png`);

  imageRef.getDownloadURL().then((url) => {
    //this.state["petImages"] = url
    //this.setState(this.state)
    return(url);
  })
  .catch((error) => {
    defaultImageRef.getDownloadURL().then((url) => {

      return(url);
    })
  })
}

function HasProfilePic(props) {

  //console.log(getImage(String(props.AccountId) + String(props.PetId)));
  // Check if file exists
  var url = getImage(String(props.AccountId) + String(props.PetId))
  console.log(url);
  return <img src={url} id="ImageCircle"></img>;


  

  return <p id="PetText">Add Pet</p>;
}

export default class Pets extends Component {

    state = { pets: [], 
              account: {},
              petImages: [] }

    componentDidMount() {

        Promise.all([
          axios.get("/api/pets/"), 
          axios.get("/api/accounts/1/")
        ]).then(([result1, result2]) => {
            const pets = result1.data;
            const account = result2.data;

            this.setState({  pets, account });
          })
          .catch((error) => {
            console.log(error);
          })
    }



    render() {
    const account = this.state.account;
    return (
        <div className="fullPageContainer">
          <div>
            <Header />
          </div>
          <Container fluid>
            <div className="mainPageBody nopadding">
                <div className="mainPageContents">
                  { this.state.pets.map(pet => 
                  <Link id="PetLink" to={"/Pets/Profile/" + pet.PetId}>
                    <div id="PetContainer">
                      <div id="CircleShape">
                        <HasProfilePic AccountId={account.AccountId} PetId={pet.PetId}/>
                      </div>
                      <div>
                          <p id="NameText">{pet.PetName + account.FirstName}</p> 
                      </div>
                    </div>
                  </Link>
                  )}
                </div>
            </div>
          </Container>
          <div className="mainPageFooter">
            <Footer />
          </div> 
        </div>
    )
    }
}

//export default Pets
