// PetHealth.js

import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';

import Header from './Header.js';
import Footer from './Footer.js';

import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

//function PetProfile() {
  
export default class PetHealth extends Component {
  
  state = { petWeight: 0,}

  handleChange = event => {
    this.setState({ petWeight: event.target.value });
  }

  handleSubmit = event => {
    event.preventDefault();

    const { match: { params } } = this.props;

    const weight = {
      petWeight: this.state.petWeight
    };

    axios.post(`http://localhost:5000/api/pet-weights/${params.PetId}`, { weight })
        .then(response=>{
            console.log(response);
            console.log(response.data);
        })
        .catch((error) => {
            console.log(error);
        })

  }

  render() {
    const { pet } = this.state;
    return (
      <div className="fullPageContainer">
        <div>
          <Header />
        </div>

        <Container fluid className="petProfileWindow">
          <div className="petProfileCard">
            <Row>
              <Col>
                <Link to="/pets">
                <button className="btn-petprofile-nav">
                    &lt;&lt;Back
                </button>
                </Link>
              </Col>
                  name: { pet.PetName } <br />
                  gender: { pet.PetGender } <br />
              <Col>
              </Col>
              <Col>
              </Col>
            </Row>
          </div>

          <div className="petProfileNavBar">
              <Row>
              <Col sm="1">
              </Col>
              <Col>
                  <Link to="/petrecords">
                    <button className="btn-petprofile-nav">
                      Records
                    </button>
                  </Link>
                  <Link to="/pethealth">
                    <button className="btn-petprofile-nav">
                      Health
                    </button>
                  </Link>
                  <Link to="/petevents">
                    <button className="btn-petprofile-nav">
                      Events
                    </button>
                  </Link>
                  <Link to="/petreminders">
                    <button className="btn-petprofile-nav">
                      Reminders
                    </button>
                  </Link>
              </Col>
              <Col sm="1">
              </Col>
              </Row>
          </div>
        <div className="petProfileBody">
          <form onSubmit={this.handleSubmit}>
            <label>
              new pet weight:
              <input type="text" name="weight" onChange={this.handleChange} />
            </label>
            <button type="submit"> add weight </button>
          </form>
        </div>
        </Container>

        <div className="mainPageFooter">
          <Footer />
        </div> 

      </div>
    )
}
}
