// PetProfile.js

import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';

import Header from './Header.js';
import Footer from './Footer.js';

import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

//function PetProfile() {
export default class PetProfile extends Component {

  state = { pet: [] }

  componentDidMount() {
    axios.get("http://localhost:5000/api/pets/1")
        .then(response=>{
            console.log(response);
            const pet = response.data;
            this.setState({ pet });
        })
        .catch((error) => {
            console.log(error);
        })
}

  render() {
    return (
      <div className="fullPageContainer">
        <div>
          <Header />
        </div>

        <Container fluid className="petProfileWindow">
          <div className="petProfileCard">
            <Link to="/pets">
              <button className="btn-petprofile-nav">
                  &lt;&lt;Back
              </button>
            </Link>
            <div>
            { this.state.pet(pet => 

              <p>{pet.PetName}</p>

              )}
            </div>
          </div>

          <div className="petProfileNavBar">
              <Row>
              <Col sm="1">
              </Col>
              <Col>
                  <Link to="/petrecords">
                    <button className="btn-petprofile-nav">
                      Records
                    </button>
                  </Link>
                  <Link to="/pethealth">
                    <button className="btn-petprofile-nav">
                      Health
                    </button>
                  </Link>
                  <Link to="/petevents">
                    <button className="btn-petprofile-nav">
                      Events
                    </button>
                  </Link>
                  <Link to="/petreminders">
                    <button className="btn-petprofile-nav">
                      Reminders
                    </button>
                  </Link>
              </Col>
              <Col sm="1">
              </Col>
              </Row>
          </div>
        <div className="petProfileBody">
            <h4> Pet Profile details here - Pet Profile details here - Pet Profile details here <br />
            Pet Profile details here - Pet Profile details here - Pet Profile details here <br />
            Pet Profile details here - Pet Profile details here - Pet Profile details here <br />
            Pet Profile details here - Pet Profile details here - Pet Profile details here <br />
            Pet Profile details here - Pet Profile details here - Pet Profile details here <br />
            Pet Profile details here - Pet Profile details here - Pet Profile details here <br />
            </h4>
        </div>
        </Container>
        <div className="mainPageFooter">
          <Footer />
        </div> 
      </div>
    )
}
}

//export default PetProfile
